    use super::{alt_bn128_add, alt_bn128_mul, alt_bn128_pairing};

    pub fn hex2bin(s: &str, output: &mut [u8]) {
        for i in (0..s.len()).step_by(2) {
            let b = u8::from_str_radix(&s[i..i + 2], 16).unwrap();
            output[i / 2] = b;
        }
    }

